mapper.select:
select no, sidoName, gugunName, dongName, dongCode, lat, lng
from baseaddress
where gugunName=#{gugun};

select no, sidoName, gugunName, dongName, dongCode, lat, lng
from baseaddress
where dongName=#{dong};

select aptCode, aptName, dongName, lat, lng
from houseinfo
where dongName=#{dong};

select aptCode, aptName, dongName, lat, lng
from houseinfo
where aptName like concat('%',#{aptNAme},'%');
