mapper.select:
select no, sidoName, gugunName, dong, dongCode, lat, lng
from baseaddress
where gugunName=#{gugun};

select no, sidoName, gugunName, dong, dongCode, lat, lng
from baseaddress
where dong=#{dong};

select aptCode, apartmentName, dong, lat, lng
from houseinfo
where dong like concat('%',#{dong},'%');

select aptCode, apartmentName, dong, lat, lng
from houseinfo
where aptName like concat('%',#{aptName},'%');
